import React from "react";
import { SimpleImg } from "react-simple-img";

const SpeakerImage = ({ firstName, lastName, id }) => {
  return (
    <SimpleImg
      src={`/speakers/speaker-${id}.jpg`}
      animationDuration="1"
      width={200}
      height={200}
      applyAspectRatio="true"
      alt={`${firstName} ${lastName}`}
    />
  );
};

export default SpeakerImage;
