import React from "react";

const Speakers = () => {
  const speak = [
    { imageSrc: "speaker-component-1124", name: "Douglas CorckFord" },
    { imageSrc: "speaker-component-1530", name: "Tamara Baker" },
    { imageSrc: "speaker-component-10803", name: "Eugene Chuvyrov" },
  ];

  return (
    <SpeakerRenderProps>
      {() => {
        <div>
          {speak.map(({ imageSrc, name }) => {
            return (
              <>
                <img
                  src={`/images/${imageSrc}.png`}
                  alt={name}
                  key={imageSrc}
                />
              </>
            );
          })}
        </div>;
      }}
    </SpeakerRenderProps>
  );
};

export default Speakers;
