const withData = (maxSpeakersToShow) => (Component) => {
  const speak = [
    { imageSrc: "speaker-component-1124", name: "Douglas CorckFord" },
    { imageSrc: "speaker-component-1530", name: "Tamara Baker" },
    { imageSrc: "speaker-component-10803", name: "Eugene Chuvyrov" },
  ];
  return function () {
    const limitSpeakers = speak.slice(0, maxSpeakersToShow);
    return <Component speak={limitSpeakers}></Component>;
  };
};

export default withData;

