import React from "react";
import SpeakerRenderProps from "./SpeakerRenderProps";

const Speakers = () => {
  return (
    <SpeakerRenderProps>
      {({ speak }) => {
        return (
          <div>
            {speak.map(({ imageSrc, name }) => {
              return (
                <>
                  <img
                    src={`/images/${imageSrc}.png`}
                    alt={name}
                    key={imageSrc}
                  />
                </>
              );
            })}
          </div>
        );
      }}
    </SpeakerRenderProps>
  );
};

export default Speakers;
